class ColorConfig:
    def __init__(
        self,
        info: str = 'B4AEA8',
        success: str = '3EA966',
        fail: str = 'C8553D',
        warning: str = 'F28F3B',
        error: str = 'A22B24',
        process: str = '2BC4E9',
        dim: str = '918B86'
    ):
        self.info = info
        self.success = success
        self.fail = fail
        self.warning = warning
        self.error = error
        self.process = process
        self.dim = dim